import Head from "next/head";
import React from "react";
import Layout from "@/components/Layout/Layout";
import YellowBtn from "@/components/uikit/YellowBtn/YellowBtn";
import s from "./index.module.scss";
import { useRouter } from "next/router";
import { getProductLinks } from "@/lib/apiFunctions";

export default function Index({ footerLinks }) {
  const router = useRouter();
  return (
    <>
      <Head>
        <title itemprop="headline">Странница отсутствует</title>
        <meta name="robots" content="noindex, nofollow" />
      </Head>
      <Layout footerLinks={footerLinks}>
        <section className={s.section}>
          <div className={s.container}>
            <h1 className={s.head}>404</h1>
            <p className={s.title}>Страница не найдена...</p>
            <YellowBtn
              className={s.btn}
              onClick={() => {
                router.push("/");
              }}
            >
              На главную
            </YellowBtn>
          </div>
          <ColoredIcon className={s.colored_icon} />
          <PaperIcon className={s.paper_icon} />
          <ShtampIcon className={s.shtamp_icon} />
          <Star className={s.first_star} />
          <Star className={s.second_star} />
          <Jet className={s.jet} />
        </section>
      </Layout>
    </>
  );
}

export const getStaticProps = async () => {
  const footerLinks = await getProductLinks();
  return {
    props: {
      footerLinks,
    },
  };
};

